import React, { useState } from "react";

import Input from "../../shared/components/FormElements/Input";
import { VALIDATOR_EMAIL, VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from "../../shared/util/validators";
import useForm from "../../shared/hooks/form-hook";
import Button from "../../shared/components/FormElements/Button";
import Card from "../../shared/components/UIElements/Card";
import './Auth.css';

const Authenticate = (props) => {

    const [isLoginMode, setIsLoginMode] = useState(true);

    //keep track of formstate
    const [formState, inputHandler, setFormData] = useForm({
        email: {
            value: '',
            isValid: false
        },
        password: {
            value: '',
            isValid: false
        }
    }, false);

    const switchModeHandler = () => {
        if (!isLoginMode) {
            setFormData({
                name: undefined
            }, formState.inputs.email.isValid && formState.inputs.password.isValid);
        }else{
            setFormData({
                ...formState,
                name: {
                    value: '',
                    isValid: false
                }
            }, false);
        }
        setIsLoginMode(prevMode => !prevMode);
    }

    //function that gets called whenever the button gets clicked
    const userAuthInputHandler = (event) => {
        event.preventDefault();
        console.log(formState.inputs);
    }


    return (
        <Card className="authentication">
            <h2>Login Required</h2>
            <hr />
            <form onSubmit={userAuthInputHandler}>
                {!isLoginMode && (
                    <Input
                        element='input'
                        id='name'
                        type='text'
                        label="Your Name"
                        validators={VALIDATOR_REQUIRE}
                        errorText="Please enter your name"
                        onInput={inputHandler}
                    />
                )}
                <Input
                    id='email'
                    element='input'
                    type='email'
                    label='Email'
                    validators={[VALIDATOR_EMAIL()]}
                    errorText="Please enter a valid email"
                    onInput={inputHandler}
                />
                <Input
                    id='password'
                    element='input'
                    type='password'
                    label='Password'
                    validators={[VALIDATOR_MINLENGTH(5)]}
                    errorText="Please enter a valid password (at least 5 characters long)"
                    onInput={inputHandler}
                />
                <Button type='submit' disabled={!formState.isValid}>
                    {isLoginMode ? 'LOGIN' : 'SIGNUP'}
                </Button>
                <Button inverse onClick={switchModeHandler}>SWITCH TO {isLoginMode ? "SIGNUP" : "LOGIN"}</Button>
            </form>
        </Card>
    )
}

export default Authenticate;
