import React from "react";

import Input from "../../shared/components/FormElements/Input";
import { VALIDATOR_EMAIL, VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import useForm from "../../shared/hooks/form-hook";
import Button from "../../shared/components/FormElements/Button";

const Authenticate = (props) => {

    const [formState, inputHandler, setFormData] = useForm({
        email: {
            value: '',
            isValid: false
        },
        password: {
            value: '',
            isValid: false
        }
    }, false);

        //useEffect to make sure setFormData isnt called on every reload which could create a infinite loop
        useEffect(() => {
            //make sure the identified place was found
            if (identifiedPlace) {
                setFormData({
                    title: {
                        value: identifiedPlace.title,
                        isValid: true
                    },
                    description: {
                        value: identifiedPlace.description,
                        isValid: true
                    }
                }, true);
            }
    
            setIsLoading(false);
        }, [setFormData, identifiedPlace]);

    const userAuthInputHandler = (event) => {
        event.preventDefault();
        console.log(formState.inputs);
    }

    return (
        <form className="authentication-form">
            <Input
                id='email'
                element='input'
                type='text'
                label='Email'
                validators={[VALIDATOR_EMAIL()]}
                errorText="please enter a valid email"
                onInput={inputHandler}
                initialValue={formState.inputs.email.value}
            />
            <Input
                id='password'
                element='input'
                type='text'
                label='Password'
                validators={[VALIDATOR_MINLENGTH(5)]}
                errorText="please enter a valid password (at least 5 characters long)"
                onInput={inputHandler}
                initialValue={formState.inputs.password.value}
            />
            <Button type='submit' disabled={!formState.isValid} onClick={userAuthInputHandler}>
                UPDATE PLACE
            </Button>
        </form>
    )
}

export default Authenticate;
