import React, { useCallback, useReducer } from "react";

import Input from "../../shared/components/FormElements/Input";
import './NewPlace.css';
import { VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from "../../shared/util/validators";
import Button from "../../shared/components/FormElements/Button";

const formReducer = (state, action) => {
    switch (action.type) {
        case 'INPUT_CHANGE':
            let formIsValid = true;
            //check all inputs and update formisvalid if all actions that changed are valid or not
            for (const inputId in state.inputs) {
                if (inputId === action.inputId) {
                    formIsValid = formIsValid && action.isValid;
                } else {
                    formIsValid = formIsValid && state.inputs[inputId].isValid;
                }
            }
            return {
                ...state,
                inputs: {
                    ...state.inputs,
                    [action.inputId]: { value: action.value, isValid: action.isValid }
                },
                isValid: formIsValid
            };

        default:
            return state;
    }
};

const NewPlace = () => {

    const [formState, dispatch] = useReducer(formReducer, {
        inputs: {
            title: {
                value: '',
                isValid: false
            },
            description: {
                value: '',
                isValid: false
            }
        },
        isValid: false
    });

    const placeSubmitHandler = event => {
        event.preventDefault();
        console.log(formState.inputs);//will be sent to backend
    };

    //usecallback so that the function doesnt get remade everytime the page is reloaded
    const inputHandler = useCallback((id, value, isValid) => {
        dispatch({type:'INPUT_CHANGE', value:value, isValid: isValid, inputId: id})
    }, []);

    return (
        <form className="place-form" onSubmit={placeSubmitHandler}>
            <Input
                id="title"
                type="text"
                label="Title"
                element="input"
                validators={[VALIDATOR_REQUIRE()]}
                errorText='Please Enter A Valid Title'
                onInput={inputHandler}
            />
            <Input
                id='description'
                label="Description"
                element="textarea"
                validators={[VALIDATOR_MINLENGTH(5)]}
                errorText='Please Enter A Valid Description (at least 5 characters)'
                onInput={inputHandler}
            />
            <Button type='submit' disabled={!formState.isValid} >
                ADD PLACE
            </Button>
        </form>
    )
};

export default NewPlace;
