import React, { useReducer } from "react";

import './Input.css';
import { validate } from "../../util/validators";

const inputReducer = (state, action) => {
    switch (action.type) {
        case 'CHANGE':
            return {
                ...state,//keep old state
                value: action.val,
                isValid: validate(action.val, action.validators)
            };
        case 'TOUCH': 
        return {
            ...state,
            isTouched: true
        }

        //return unchanged state on default
        default:
            return state;
    }
}

const Input = props => {
    //use useReducer to manage complex state/interconnnected state
    //pass it a function and initial state
    const [inputState, dispatch] = useReducer(inputReducer, { value: '', isValid: false, isTouched: false });

    const changeHandler = event => {
        dispatch({ type: 'CHANGE', val: event.target.value, validators: props.validators });
    };

    const touchHandler = () => {
        dispatch({
            type: 'TOUCH'
        })
    }

    //if given input as a prop then render an input field otherwise just render a text area
    const element = props.element === 'input' ? (
        <input
            id={props.id}
            type={props.type}
            placeholder={props.placeholder}
            onChange={changeHandler}
            onBlur={touchHandler}
            value={inputState.value}
        />
    ) : (
        <textarea
            id={props.id}
            rows={props.rows || 3}
            onChange={changeHandler}
            onBlur={touchHandler}
            value={inputState.value}
        />
    );



    return (
        <div className={`form-control ${!inputState.isValid && inputState.isTouched && 'form-control--invalid'}`}>
            <label htmlFor={props.id}>{props.label}</label>
            {element}
            {!inputState.isValid && <p>{props.errorText}</p>}
        </div>
    )
}

export default Input;
